
public class OperEx7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		! 부정연산자 
		boolean power = false;
		
		System.out.println(power);
		
		power = !power;	//! 붙여서 false 를부정시켜 true 결과나옴
		System.out.println(power);
		
		power = !power;
		System.out.println(power);
	}

}
